mapper.insert:

/* 애니 조회수 증가 쿼리 */

insert into web_app_test2.tb_anime_view(user_no, r_anime_no)
    value (#{userNo},#{animeNo})


/* 애니 정보 삽입 쿼리 */
insert into web_app_test2.tb_anime(m_n_no, title, author, link, finalized_yn, anime_broadcast_cnt, anime_reg_dt)
value (#{madeNatureNo}, #{title}, #{author}, #{link}, #{finalizedYnEnum}, #{animeBroadcastCnt}, #{animeRegDt})


    /* 애니 정보 조회 쿼리 */
    select max(r_anime_no)
    from web_app_test2.tb_anime


/* 애니 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_anime_thumbnail_img(r_anime_no, file_path, file_name)
value (#{animeNo}, #{filePath}, #{fileName})

mapper.update:

/* 단일 애니 정보 수정 쿼리 */
update web_app_test2.tb_anime
set m_n_no              = #{madeNatureNo}
  , title               = #{animeTitle}
  , author              = #{animeAuthor}
  , link                = #{link}
  , finalized_yn        = #{finalizedYnEnum}
  , anime_broadcast_cnt = #{animeBroadcastCnt}
  , anime_reg_dt        = #{animeRegDt}
  , mod_dt              = now()
where use_yn = 'y'
and r_anime_no = #{animeNo}


/* 애니 썸네일 수정 쿼리 */
update web_app_test2.tb_anime_thumbnail_img
set file_path = #{filePath}
  , file_name = #{fileName}
  , mod_dt    = now()
where r_anime_no = #{animeNo}


/* 단일 애니 상태 수정 쿼리 */
update web_app_test2.tb_anime
