mapper.select:
/* (조건을 기반해서)전체 책 개수 조회 쿼리 */
select count(*) from web_app_test2.tb_book as tb_b where use_yn = 'y'
and tb_a.title like concat('%',#{title}, '%')

mapper.delete:

/* 디비에 쌓여있는 정체 책 삭제 쿼리 */

delete
from web_app_test2.tb_book

mapper.select:
/* 전체 책 리스트 조회 쿼리(pagination) */
select tb_b.r_book_no as bookNo , tb_b.m_n_no as madeNatureNo , tb_b_t.tag_korean_name as bookTypeKorean , tb_b_t.tag_english_name as bookTypeEnglish , tb_b.title as bookTitle , tb_b.author as bookAuthor , tb_b.link as link , tb_b.use_yn as bookUseYnEnum , date_format(tb_b.book_reg_dt, '%Y-%m-%d') as bookRegDt , date_format(tb_b.reg_dt, '%Y-%m-%d') as regDt from web_app_test2.tb_book as tb_b inner join web_app_test2.tb_book_type as tb_b_t on tb_b.r_book_type_no = tb_b_t.r_book_type_no left outer web_app_test2.tb_book_thumbnail_img as tb_t_i on tb_b.r_book_no = tb_t_i.r_book_no where tb_b.use_yn = 'y'
and tb_b.title like concat('%',#{title}, '%')
order by tb_b.reg_dt desc limit #{offset}, #{pageSize}


/*
    전체 책 썸네일 리스트 조회 쿼리
   (리스트 조회의 경우 left outer join 보다, 선택적인 번호만 넘겨서 부분 조회하는 것으로 개발하였습니다)
*/

select r_book_no as bookNo
        , web_thumbnail_url as webThumbnailUrl
from web_app_test2.tb_book_thumbnail_img
where r_book_no in (

        #{item}

)


/*
    bookNo 기반 썸네일 개수 조회
*/

select count(*)
    from web_app_test2.tb_book_thumbnail_img
where r_book_no = #{bookNo}

mapper.insert:

/* 책 조회수 증가 쿼리 */

insert into web_app_test2.tb_book_view(user_no, r_book_no)
    value (#{userNo}, #{bookNo})


/* 책 정보 삽입 쿼리 */
insert into web_app_test2.tb_book(m_n_no, r_book_type_no, title, author, link, book_reg_dt)
value (#{madeNatureNo}, #{bookTypeNo}, #{title}, #{author}, #{link}, #{bookRegDt})


    /* 책 정보 삽입 쿼리 */
    select max(r_book_no)
    from web_app_test2.tb_book


/* 책 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_book_thumbnail_img(r_book_no, web_thumbnail_url)
    value (#{bookNo}, #{webThumbnailUrl})

mapper.update:

/* 단일 책 정보 수정 쿼리 */
update web_app_test2.tb_book
set m_n_no         = #{madeNatureNo}
  , r_book_type_no = #{bookTypeNo}
  , title          = #{title}
  , author         = #{author}
  , link           = #{link}
  , book_reg_dt    = #{bookRegDt}
  , mod_dt         = now()
where use_yn = 'y'
and r_book_no = #{bookNo}


/* 책 썸네일 수정 쿼리 */
update web_app_test2.tb_book_thumbnail_img
set web_thumbnail_url = #{webThumbnailUrl}
  , mod_dt    = now()
where r_book_no = #{bookNo}


/* 단일 책 상태 수정 쿼리 */
update web_app_test2.tb_book
set use_yn = #{bookUseYnEnum}
  , mod_dt = now()
where r_book_no = #{bookNo}

mapper.delete:

/* 단일 책 삭제 쿼리 */
delete
from web_app_test2.tb_book
where r_book_no = #{bookNo}


/* 단일 책 썸네일 삭제 쿼리 */
delete
from web_app_test2.tb_book_thumbnail_img
where r_book_no = #{bookNo}
