mapper.select:

/* 전체 책 개수 조회 쿼리 */

select count(*)
from web_app_test2.tb_book


/* 전체 책 썸네일 리스트 조회 쿼리(리스트 조회의 경우 left outer join 보다, 선택적인 번호만 넘겨서 부분 조회하는 것으로 개발하였습니다) */

select r_book_no as bookNo
     , concat(file_path, '/', file_name) as filePullPath
from web_app_test2.tb_book_thumbnail_img
where r_book_no in (

        #{item}

)

mapper.insert:

/* 책 조회수 증가 쿼리 */

insert into web_app_test2.tb_book_view(user_no, r_book_no)
value(#{userNo}, #{bookNo})


/* 책 정보 삽입 쿼리 */
insert into web_app_test2.tb_book(m_n_no, r_book_type_no, title, author, link, book_reg_dt)
value (#{madeNatureNo}, #{bookTypeNo}, #{title}, #{author}, #{link}, #{bookRegDt})


    /* 책 정보 삽입 쿼리 */
    select max(r_book_no)
    from web_app_test2.tb_book


/* 책 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_book_thumbnail_img(r_book_no, file_path, file_name)
    value(#{bookNo}, #{filePath}, #{fileName})

mapper.update:

/* 단일 책 정보 수정 쿼리 */
update web_app_test2.tb_book
set m_n_no = #{madeNatureNo}
    , r_book_type_no = #{bookTypeNo}
    , title = #{title}
    , author = #{author}
    , link = #{link}
    , book_reg_dt = #{bookRegDt}
    , mod_dt = now()
where r_book_no = #{bookNo}


/* 책 썸네일 수정 쿼리 */
update web_app_test2.tb_book_thumbnail_img
set file_path = #{filePath}
  , file_name = #{fileName}
  , mod_dt = now()
where r_book_no = #{bookNo}


/* 단일 책 상태 수정 쿼리 */
update web_app_test2.tb_book
set use_yn = #{bookUseYnEnum}
  , mod_dt = now()
where r_book_no = #{bookNo}

mapper.delete:

/* 단일 책 삭제 쿼리 */
delete from web_app_test2.tb_book
where r_book_no = #{bookNo}


/* 단일 책 썸네일 삭제 쿼리 */
delete from web_app_test2.tb_book_thumbnail_img
where r_book_no = #{bookNo}
