mapper.select:

/* 메인 만화책 리스트 조회 쿼리 */
select r_book_no as comicBookNo
     , title     as comicBookTitle
     , author    as comicBookAuthor
from web_app_test2.tb_comic_book
where use_yn = 'y'
  and date_format(book_reg_dt, '%Y-%m-%d') > #{startDt}
  and date_format(book_reg_dt, '%Y-%m-%d') < #{endDt}
limit 10

mapper.delete:

/* 디비에 쌓여있는 전체 만화책 삭제 쿼리 */

delete
from web_app_test2.tb_comic_book

mapper.select:

/*
    전체 만화책 썸네일 리스트 조회 쿼리
   (리스트 조회의 경우 left outer join 보다, 선택적인 번호만 넘겨서 부분 조회하는 것으로 개발하였습니다)
*/

select r_book_no as bookNo
, concat(file_path, '/', file_name) as filePullPath
from web_app_test2.tb_comic_book_thumbnail_img
where r_book_no in (

    #{item}

)

mapper.insert:

/* 만화책 조회수 증가 쿼리 */

insert into web_app_test2.tb_comic_book_view(user_no, r_book_no)
    value (#{userNo},#{bookNo})


/* 만화책 정보 삽입 쿼리 */
insert into web_app_test2.tb_comic_book(m_n_no, title, author, link, serial_state, book_reg_dt)
value (#{madeNatureNo}, #{title}, #{author}, #{link}, #{comicBookSerialStateEnum}, #{comicBookRegDt})


    /* 만화책 정보 삽입 쿼리 */
    select max(r_book_no)
    from web_app_test2.tb_comic_book


/* 만화책 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_comic_book_thumbnail_img(r_book_no, file_path, file_name)
    value (#{bookNo}, #{filePath}, #{fileName})

mapper.update:

/* 단일 만화책 정보 수정 쿼리 */
update web_app_test2.tb_comic_book
set m_n_no       = #{madeNatureNo}
  , title        = #{title}
  , author       = #{author}
  , link         = #{link}
  , serial_state = #{comicBookSerialStateEnum}
  , book_reg_dt  = #{comicBookRegDt}
  , mod_dt       = now()
where use_yn = 'y'
  and r_book_no = #{bookNo};


/* 만화책 썸네일 수정 쿼리 */
update web_app_test2.tb_comic_book_thumbnail_img
set file_path = #{filePath}
  , file_name = #{fileName}
  , mod_dt    = now()
where r_book_no = #{bookNo}


/* 단일 만화책 상태 수정 쿼리 */
update web_app_test2.tb_comic_book
set use_yn = #{comicBookUseYnEnum}
  , mod_dt = now()
where use_yn = 'y'
  and r_book_no = #{bookNo}

mapper.delete:

/* 단일 만화책 삭제 쿼리 */
delete
from web_app_test2.tb_comic_book
where r_book_no = #{bookNo}


/* 단일 만화책 썸네일 삭제 쿼리 */
delete
from web_app_test2.tb_comic_book_thumbnail_img
where r_book_no = #{bookNo}
