mapper.select:
/* (조건을 기반해서)전체 책 개수 조회 쿼리 */
select count(*) from web_app_test2.tb_comic_book as tb_c_b where use_yn = 'y'
and tb_c_b.title like concat('%',#{title}, '%')

/* 전체 만화책 리스트 조회 쿼리(pagination) */
select tb_c_b.r_book_no as bookNo , tb_c_b.m_n_no as madeNatureNo , tb_c_b.title as comicBookTitle , tb_c_b.author as comicBookAuthor , tb_c_b.link as link , tb_c_b_i.web_thumbnail_url as webThumbnailUrl , tb_c_b.serial_state as comicBookSerialStateEnum , date_format(tb_c_b.book_reg_dt, '%Y-%m-%d') as comicBookRegDt , date_format(tb_c_b.reg_dt, '%Y-%m-%d') as regDt from web_app_test2.tb_comic_book as tb_c_b inner join web_app_test2.tb_comic_book_thumbnail_img as tb_c_b_i on tb_c_b.r_book_no = tb_c_b_i.r_book_no where tb_c_b.use_yn = 'y'
and tb_c_b.title like concat('%',#{title}, '%')
order by tb_c_b.reg_dt desc limit #{offset}, #{pageSize}


/*
    comicBookNo 기반 썸네일 개수 조회
*/

select count(*)
from web_app_test2.tb_comic_book_thumbnail_img
where r_book_no = #{comicBookNo}


/*
    전체 만화책 썸네일 리스트 조회 쿼리
   (리스트 조회의 경우 left outer join 보다, 선택적인 번호만 넘겨서 부분 조회하는 것으로 개발하였습니다)
*/

select r_book_no as bookNo
, web_thumbnail_url as webThumbnailUrl
from web_app_test2.tb_comic_book_thumbnail_img
where r_book_no in (

    #{item}

)

mapper.insert:

/* 만화책 조회수 증가 쿼리 */

insert into web_app_test2.tb_comic_book_view(user_no, r_book_no)
    value (#{userNo},#{bookNo})


/* 만화책 정보 삽입 쿼리 */
insert into web_app_test2.tb_comic_book(m_n_no, title, author, link, serial_state, book_reg_dt)
value (#{madeNatureNo}, #{title}, #{author}, #{link}, #{comicBookSerialStateEnum}, #{comicBookRegDt})


    /* 만화책 정보 삽입 쿼리 */
    select max(r_book_no)
    from web_app_test2.tb_comic_book


/* 만화책 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_comic_book_thumbnail_img(r_book_no, web_thumbnail_url)
    value (#{bookNo}, #{webThumbnailUrl})

mapper.update:

/* 단일 만화책 정보 수정 쿼리 */
update web_app_test2.tb_comic_book
set m_n_no       = #{madeNatureNo}
  , title        = #{title}
  , author       = #{author}
  , link         = #{link}
  , serial_state = #{comicBookSerialStateEnum}
  , book_reg_dt  = #{comicBookRegDt}
  , mod_dt       = now()
where use_yn = 'y'
and r_book_no = #{bookNo};


/* 만화책 썸네일 수정 쿼리 */
update web_app_test2.tb_comic_book_thumbnail_img
set web_thumbnail_url = #{webThumbnailUrl}
  , mod_dt    = now()
where use_yn = 'y'
and r_book_no = #{bookNo}


/* 단일 만화책 상태 수정 쿼리 */
update web_app_test2.tb_comic_book
set use_yn = #{comicBookUseYnEnum}
  , mod_dt = now()
where use_yn = 'y'
and r_book_no = #{bookNo}

mapper.delete:

/* 단일 만화책 삭제 쿼리 */
delete
from web_app_test2.tb_comic_book
where r_book_no = #{bookNo}


/* 단일 만화책 썸네일 삭제 쿼리 */
delete
from web_app_test2.tb_comic_book_thumbnail_img
where r_book_no = #{bookNo}


/* 디비에 쌓여있는 전체 만화책 삭제 쿼리 */

delete
from web_app_test2.tb_comic_book
