mapper.select:

 /*
 전체 드라마 썸네일 리스트 조회 쿼리
(리스트 조회의 경우 left outer join 보다, 선택적인 번호만 넘겨서 부분 조회하는 것으로 개발하였습니다)
 */

 select r_drama_no as dramaNo
      , concat(file_path, '/', file_name) as fileFullPath
 from web_app_test2.tb_drama_thumbnail_img
 where r_drama_no in (

         #{item}

 )

mapper.insert:

/* 드라마 조회수 증가 쿼리 */

insert into web_app_test2.tb_drama_view(user_no, r_drama_no)
value(#{userNo},#{dramaNo})


/* 드라마 정보 삽입 쿼리 */
insert into web_app_test2.tb_drama(m_n_no, title, author, link, broadcast_state, drama_reg_dt)
value (#{madeNatureNo}, #{title}, #{author}, #{link}, #{broadcastStateEnum}, #{dramaRegDt})


    /* 드라마 dramaNo 반환 쿼리 */
    select max(r_drama_no)
    from web_app_test2.tb_drama


/* 드라마 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_drama_thumbnail_img(r_drama_no, file_path, file_name)
    value(#{dramaNo}, #{filePath}, #{fileName})

mapper.update:

/* 단일 드라마 정보 수정 쿼리 */
update web_app_test2.tb_drama
set m_n_no = #{madeNatureNo}
    ,title = #{title}
    ,author = #{author}
    ,link = #{link}
    ,broadcast_state = #{broadcastStateEnum}
    ,drama_reg_dt = #{dramaRegDt}
    ,mod_dt = now()
where use_yn = 'y'
and r_drama_no = #{dramaNo}


/* 드라마 썸네일 수정 쿼리 */
update web_app_test2.tb_drama_thumbnail_img
set file_path = #{filePath}
  , file_name = #{fileName}
  , mod_dt = now()
where r_drama_no = #{dramaNo}


/* 단일 드라마 상태 수정 쿼리 */
update web_app_test2.tb_drama
set use_yn = #{dramaUseYnEnum}
  , mod_dt = now()
where r_drama_no = #{dramaNo}

mapper.delete:

/* 단일 드라마 삭제 쿼리 */
delete from web_app_test2.tb_drama
where r_drama_no = #{dramaNo}


/* 단일 드라마 썸네일 삭제 쿼리 */
delete from web_app_test2.tb_drama_thumbnail_img
where r_drama_no = #{dramaNo}
