mapper.select:
/* (조건을 기반해서)전체 영화 개수 조회 쿼리 */
select count(*) from web_app_test2.tb_movie as tb_m where tb_m.use_yn = 'y'
and tb_m.title like concat('%',#{title}, '%')


/*
    movieNo 기반 썸네일 개수 조회
*/

select count(*)
from web_app_test2.tb_movie_thumbnail_img
where r_movie_no = #{movieNo}

/* 전체 영화 리스트 조회 쿼리(pagination) */
select tb_m.r_movie_no as movieNo , tb_m.m_n_no as madeNatureNo , tb_m.title as movieTitle , tb_m.director as directorName , tb_m.link as link , tb_m.total_number_of_episode as totalNumberOfEpisode , tb_m_t_i.web_thumbnail_url as webThumbnailUrl , ifnull(tb_m_v.viewCnt, 0) as viewCnt , date_format(tb_m.movie_reg_dt, '%Y-%m-%d') as movieRegDt , date_format(tb_m.reg_dt, '%Y-%m-%d') as regDt from web_app_test2.tb_movie as tb_m inner join web_app_test2.tb_movie_thumbnail_img as tb_m_t_i on tb_m.r_movie_no = tb_m_t_i.r_movie_no left outer join () as tb_m_v on tb_m.r_movie_no = tb_m_v.r_movie_no where tb_m.use_yn = 'y'
and tb_m.title like concat('%',#{title}, '%')
order by tb_m.reg_dt desc limit #{offset}, #{pageSize}


/*
    전체 영화 썸네일 리스트 조회 쿼리
   (리스트 조회의 경우 left outer join 보다, 선택적인 번호만 넘겨서 부분 조회하는 것으로 개발하였습니다)
*/

select r_movie_no as movieNo
        , web_thumbnail_url as webThumbnailUrl
from web_app_test2.tb_movie_thumbnail_img
where r_movie_no in (

    #{item}

)

mapper.insert:

/* 영화 조회수 증가 쿼리 */

insert into web_app_test2.tb_movie_view(user_no, r_movie_no)
value(#{userNo},#{movieNo})


/* 영화 정보 삽입 쿼리 */
insert into web_app_test2.tb_movie(m_n_no, title, director, link, total_number_of_episode, movie_reg_dt)
value (#{madeNatureNo}, #{title}, #{directorName}, #{link}, #{totalNumberOfEpisode}, #{movieRegDt})


    /* 영화 정보 조회 쿼리 */
    select max(r_movie_no)
    from web_app_test2.tb_movie


/* 영화 썸네일 정보 삽입 쿼리 */
insert into web_app_test2.tb_movie_thumbnail_img(r_movie_no, web_thumbnail_url)
    value(#{movieNo}, #{webThumbnailUrl})

mapper.update:

/* 단일 영화 정보 수정 쿼리 */
update web_app_test2.tb_movie
set m_n_no = #{madeNatureNo}
    ,title = #{title}
    ,director = #{directorName}
    ,link = #{link}
    ,total_number_of_episode = #{totalNumberOfEpisode}
    ,movie_reg_dt = #{movieRegDt}
    ,mod_dt = now()
where use_yn = 'y'
and r_movie_no = #{movieNo}


/* 영화 썸네일 수정 쿼리 */
update web_app_test2.tb_movie_thumbnail_img
set web_thumbnail_url = #{webThumbnailUrl}
    ,mod_dt = now()
where r_movie_no = #{movieNo}


/* 단일 영화 상태 수정 쿼리 */
update web_app_test2.tb_movie
set use_yn = #{movieUseYnEnum}
    ,mod_dt = now()
where r_movie_no = #{movieNo}

mapper.delete:

/* 단일 영화 삭제 쿼리 */
delete from web_app_test2.tb_movie
where r_movie_no = #{movieNo}


/* 단일 영화 썸네일 삭제 쿼리 */
delete from web_app_test2.tb_movie_thumbnail_img
where r_movie_no = #{movieNo}


/* 디비에 쌓여있는 전체 영화 삭제 쿼리 */

delete
