mapper.select:

/* 전체 Mp3 개수 조회 쿼리 */

select count(*)
from web_app_test2.tb_mp3

mapper.insert:

/* Mp3 정보 삽입 쿼리 */
insert into web_app_test2.tb_mp3(title, singer , song_reg_dt)
value (#{title}, #{singer}, #{songRegDt})


    /* Mp3 정보 삽입 쿼리 */
    select max(r_mp3_no)
    from web_app_test2.tb_mp3

mapper.update:

/* 단일 Mp3 정보 수정 쿼리 */
update web_app_test2.tb_mp3
set title = #{title},
    singer = #{singer},
    song_reg_dt = #{songRegDt}
where r_mp3_no = #{mp3No}


/* 단일 Mp3 상태 수정 쿼리 */
update web_app_test2.tb_mp3
set use_yn = #{mp3UseYnEnum}
where r_mp3_no = #{mp3No};

mapper.delete:

/* 단일 Mp3 삭제 쿼리 */
delete from web_app_test2.tb_mp3
where r_mp3_no = #{mp3No}
