mapper.select:

/* 전체 Youtube 개수 조회 쿼리 */

select count(*)
from web_app_test2.tb_youtube

mapper.insert:

/* Youtube 정보 삽입 쿼리 */
insert into web_app_test2.tb_youtube(title, youtuber_name, like_cnt, dislike_cnt, view_cnt , youtube_reg_dt)
value (#{title}, #{youtuberName}, #{likeCnt}, #{dislikeCnt}, #{viewCnt}, #{youtubeRegDt})


    /* Youtube 정보 삽입 쿼리 */
    select max(r_youtube_no)
    from web_app_test2.tb_youtube

mapper.update:

/* 단일 Youtube 정보 수정 쿼리 */
update web_app_test2.tb_youtube
set title = #{title},
    youtuber_name = #{youtuberName},
    like_cnt = #{likeCnt},
    dislike_cnt = #{dislikeCnt},
    view_cnt = #{viewCnt}
    youtube_reg_dt = #{youtubeRegDt}
where r_youtube_no = #{youtubeNo};


/* 단일 Youtube 상태 수정 쿼리 */
update web_app_test2.tb_youtube
set use_yn = #{youtubeUseYnEnum}
where r_youtube_no = #{youtubeNo};

mapper.delete:

/* 단일 Youtube 삭제 쿼리 */
delete from web_app_test2.tb_youtube
where r_youtube_no = #{youtubeNo}
